sys_graphql_resolver.script:
(function process(/*ResolverEnvironment*/ env) {

	var caseGr = new GlideRecord('x_snc_mm_case');
	var id = env.getArguments().id;
	var stateVal = env.getArguments().state;
	var comments = env.getArguments().comments;

	caseGr.get(id);
	if (stateVal) {
		caseGr.setValue('state', stateVal);
	}

	if (comments) {
		caseGr.comments = comments;
	}

	caseGr.update();

	return caseGr;

})(env);
